const path = require('path')
const CopyPlugin = require("copy-webpack-plugin")

module.exports = {
    mode: 'development',
    entry: './src/index.js',
    output: {
        filename: 'main.js',
        path: path.resolve(__dirname, 'dist'),
    },
    devtool: 'inline-source-map',
    devServer: {
      contentBase: './dist',
    },
    // WasmThemis code is generated by Emscripten to be universal.
    // It includes some references to Node.js modules for Node.js support,
    // but they are not accessible (and not used) in web browsers.
    resolve: {
        fallback: {
            crypto: false,
            fs: false,
            path: false,
            util: false,
        }
    },
    // Bundle libthemis.wasm with the script.
    plugins: [
        new CopyPlugin({
            patterns: [
                // FIXME: use "wasm-themis" instead of "@ilammy/wasm-themis-preview"
                { from: 'node_modules/@ilammy/wasm-themis-preview/src/libthemis.wasm',
                    to: '.'
                },
            ],
        }),
    ],
    // libthemis.wasm and it's JS bundle is over 1 megabyte.
    // It's okay. Please be quiet, webpack.
    performance: {
        maxAssetSize: 2000000,
        maxEntrypointSize: 2000000,
    },
}
